    def test_to_input_format_for_subgroup_list_algorithms_3(self):
        original_file_path = "./original_file.txt"
        transformed_file_path = "./transformed_file.txt"
        content_of_the_original_file = "Description: [patient_gender = 'M'], Target: case_1 = 'yes' ; Sequence of instances tp = bitarray('') ; Sequence of instances fp = bitarray('') ; Quality Measure WRAcc = 0.01456716010385265 ; Optimistic Estimate WRAccOptimisticEstimate1 = 159.461682034136 ; tp = 853 ; fp = 228 ; TP = 1472 ; FP = 457\nDescription: [exitus = 'no'], Target: case_1 = 'yes' ; Sequence of instances tp = bitarray('') ; Sequence of instances fp = bitarray('') ; Quality Measure WRAcc = 0.01949588838177279 ; Optimistic Estimate WRAccOptimisticEstimate1 = 231.3640308964433 ; tp = 1241 ; fp = 336 ; TP = 1472 ; FP = 457\nDescription: [admission_location = 'CLINIC_REFERRAL/PREMATURE'], Target: case_1 = 'yes' ; of instances tp = bitarray('') ; Sequence of instances fp = bitarray('') ; Quality Measure WRAcc = 0.04101674773269093 ; Optimistic Estimate WRAccOptimisticEstimate1 = 116.66555055517574 ; tp = 553 ; fp = 68 ; TP = 1472 ; FP = 457\nDescription: [sofa = '[0,7)'], Target: case_1 = 'yes' ; Sequence of instances tp = bitarray('') ; Sequence of instances fp = bitarray('') ; Quality Measure WRAcc = 0.02725097627250009 ; Optimistic Estimate WRAccOptimisticEstimate1 = 208.4817699878535 ; tp = 1098 ; fp = 272 ; TP = 1472 ; FP = 457\nDescription: [sapsii = '(-25, 37]'], Target: case_1 = 'yes' ; Sequence of instances tp = bitarray('') ; Sequence of instances fp = bitarray('') ; Quality Measure WRAcc = 0.01520300367558437 ; Optimistic Estimate WRAccOptimisticEstimate1 = 139.12010624480902 ; tp = 739 ; fp = 191 ; TP = 1472 ; FP = 457\n"
        of = open(original_file_path, "w")
        of.write(content_of_the_original_file)
        of.close()
        subgroups_ok, subgroups_error = to_input_format_for_subgroup_list_algorithms(original_file_path, transformed_file_path)
        expected_content_in_transformed_file = "Description: [patient_gender = 'M'], Target: case_1 = 'yes' ;  ; \nDescription: [exitus = 'no'], Target: case_1 = 'yes' ;  ; \nERROR: subgroup in line 3 was not correctly read.\nDescription: [sofa = '[0,7)'], Target: case_1 = 'yes' ;  ; \nDescription: [sapsii = '(-25, 37]'], Target: case_1 = 'yes' ;  ; \n"
        tf = open(transformed_file_path, "r")
        real_content_in_transformed_file = tf.read()
        self.assertTrue(expected_content_in_transformed_file == real_content_in_transformed_file)
        tf.close()
        self.assertEqual(subgroups_ok, 4)
        self.assertEqual(subgroups_error, 1)
        remove(original_file_path)
        remove(transformed_file_path)
